// Extracto del sistema de renderizado 3D
export function addStellars(scene, stellars, planetsRef, composer, cameraRef) {
    stellars.forEach((stellar) => {
        const sphere = getStellarSphere(stellar);

        // Efectos especiales para estrellas
        if (stellar.star && stellar.star === true) {
            const godRaysEffect = new GodRaysEffect(cameraRef, sphere, {
                resolutionScale: 0.5,
                density: 0.96,
                decay: 0.93,
                weight: 0.3,
                samples: 60
            });
            composer.addPass(new EffectPass(cameraRef, godRaysEffect));
        }

        // Sistema de partículas para polvo espacial
        const polvoGeometry = new THREE.SphereGeometry(0.01 * sphere.geometry.parameters.radius, 32, 32);
        // ...
    });
}

// Proyección de coordenadas 3D a 2D para la interfaz
const centroCoordenadas = (i) => {
    let vectorCP = new THREE.Vector3();
    vectorCP = vectorCP.subVectors(planetsRef.current[i].position, cameraRef.current.position);
    vectorCP.setFromMatrixPosition(planetsRef.current[i].matrixWorld).project(cameraRef.current);

    const coordenadas = {x: 0, y: 0};
    coordenadas.x = (vectorCP.x + 1) / 2 * topCanvasRef.current.clientWidth;
    coordenadas.y = (1 - vectorCP.y) / 2 * topCanvasRef.current.clientHeight;

    return coordenadas;
}
