-- Radius search: find restaurants within a 5km radius
SELECT * FROM restaurants WHERE location WITHIN ((10.5, 20.3), 5.0);

-- Geometric intersection search
SELECT * FROM restaurants WHERE area INTERSECTS "POLYGON((0 0, 10 0, 10 10, 0 10, 0 0))";

-- 3-Nearest Neighbors search
SELECT * FROM restaurants WHERE location NEAREST (0, 0) LIMIT 3;

-- Rectangular spatial range search
SELECT * FROM restaurants WHERE location IN_RANGE ((0, 0), (10, 10));
